// src/components/SearchBar.js
import React, { useState } from "react";
import axios from "axios";

function SearchBar() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);

  const handleSearch = async () => {
    try {
      setHasSearched(true);
      console.log("API URL:", process.env.REACT_APP_API_URL);

      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/candidates/search?q=${query}`
      );

      console.log("API Response:", res.data);
      setResults(res.data.data || []);
    } catch (err) {
      console.error("Error:", err);
      setResults([]);
    }
  };

  return (
    <div className="p-4">
      <div className="flex items-center mb-4 gap-2">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Cari skill, nama, email..."
          className="border rounded p-2 flex-1"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Cari
        </button>
      </div>

      {results.length > 0 ? (
        <div className="overflow-x-auto mt-6 shadow-lg rounded-lg border border-gray-700 bg-[#1e293b]">
    <table className="min-w-full text-sm text-gray-100">
      <thead className="bg-gray-800 text-gray-200 uppercase text-xs">
        <tr>
          <th className="px-4 py-3 text-left">Nama</th>
          <th className="px-4 py-3 text-left">Email</th>
          <th className="px-4 py-3 text-left">Telepon</th>
          <th className="px-4 py-3 text-center">Match Score</th>
          <th className="px-4 py-3 text-center">Status</th>
          <th className="px-4 py-3 text-left">Hard Skills</th>
        </tr>
      </thead>
      <tbody>
        {results.map((candidate, index) => (
          <tr
            key={candidate.id || index}
            className={`border-t border-gray-700 ${
              index % 2 === 0 ? "bg-gray-900/50" : "bg-gray-800/50"
            } hover:bg-gray-700/40 transition-colors`}
          >
            <td className="px-4 py-3">{candidate.name}</td>
            <td className="px-4 py-3">{candidate.email}</td>
            <td className="px-4 py-3">{candidate.phone}</td>
            <td className="px-4 py-3 text-center font-semibold text-blue-400">
              {candidate.match_score}
            </td>
            <td
              className={`px-4 py-3 text-center font-medium ${
                candidate.status?.includes("passed")
                  ? "text-green-400"
                  : "text-red-400"
              }`}
            >
              {candidate.status?.replace("_", " ")}
            </td>
            <td className="px-4 py-3 text-gray-300">
              {candidate.hard_skills?.join(", ") || "-"}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
      ) : hasSearched ? (
        <p className="text-gray-400 mt-4 italic text-sm text-center">
          Tidak ada kandidat ditemukan.
        </p>
      ) : null}
    </div>
  );
}

export default SearchBar;
