// export default App;

// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import './App.css'; // Anda bisa menambahkan styling di sini
import axios from 'axios';

import SearchBar from "./components/SearchBar";


function App() {
  // State untuk menyimpan pesan dari backend
  const [backendMessage, setBackendMessage] = useState('');

  // State untuk file yang dipilih dan deskripsi pekerjaan
  const [selectedFile, setSelectedFile] = useState(null);
  const [jobDescription, setJobDescription] = useState('');

  // State untuk menyimpan hasil analisis
  const [analysisResult, setAnalysisResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [searchResults, setSearchResults] = useState([]);

  // 1. Tes koneksi ke backend saat komponen pertama kali dimuat
  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/hr/test')
      .then(response => response.json())
      .then(data => {
        console.log(data); // Cek console browser
        setBackendMessage(data.message);
      })
      .catch(error => console.error("Error fetching test data:", error));
  }, []); // Array kosong berarti efek ini hanya berjalan sekali

  const handleSearch = async (query) => {
    try {
      const response = await axios.get('http://127.0.0.1:5000/api/hr/candidates/search', {
        params: { q: query }
      });
      setSearchResults(response.data);
    } catch (error) {
      console.error("Error saat mencari kandidat:", error);
      alert("Gagal mencari kandidat. Pastikan backend jalan & API /api/search ada.");
    }
  };

  // 2. Fungsi untuk menangani submit form
  const handleSubmit = async (event) => {
    event.preventDefault(); // Mencegah form reload halaman
    if (!selectedFile) {
      alert("Silakan pilih file CV terlebih dahulu!");
      return;
    }

    setIsLoading(true);
    setAnalysisResult(null);

    const formData = new FormData();
    formData.append('cv', selectedFile);
    formData.append('jobDescription', jobDescription);

    try {
      const response = await fetch('http://127.0.0.1:5000/api/upload', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();

      if(response.ok) {
        setAnalysisResult(result);
      } else {
        alert(result.error || "Terjadi kesalahan pada server.");
      }

    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Gagal terhubung ke server.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸš€ Smart CV Analyzer & Optimizer</h1>
        
        <p style={{ fontSize: '0.8em', color: '#aaa' }}>
          Status Koneksi: <span style={{ color: 'lightgreen' }}>{backendMessage}</span>
        </p>
        <SearchBar />
        {searchResults.length > 0 && (
          <div className="w-full max-w-4xl mx-auto mt-6 p-4 bg-gray-800/60 rounded-lg shadow-lg">
            <div className="grid grid-cols-5 gap-4 text-xs font-bold text-gray-400 uppercase tracking-wider border-b border-gray-700 pb-2 mb-2">
              <div className="col-span-1">Nama</div>
              <div className="col-span-1">Email</div>
              <div className="col-span-1">Telepon</div>
              <div className="col-span-1 text-center">Match Score</div>
              <div className="col-span-1">Hard Skills</div>
            </div>
            
            {searchResults.map((candidate) => (
              <div key={candidate.id} className="grid grid-cols-5 gap-4 py-2 border-b border-gray-700/50 text-sm text-gray-200">
                <div className="col-span-1 font-medium">{candidate.name || candidate.extracted_name}</div>
                <div className="col-span-1 text-gray-400">{candidate.email || candidate.extracted_email}</div>
                <div className="col-span-1 text-gray-400">{candidate.phone || candidate.extracted_phone}</div>
                <div className="col-span-1 text-center font-bold text-lg text-cyan-400">{candidate.match_score}</div>
                <div className="col-span-1 text-gray-300">
                  {/* Asumsi hard_skills adalah array string. Jika bukan, sesuaikan. */}
                  {candidate.hard_skills ? candidate.hard_skills.join(", ") : candidate.status} 
                </div>
              </div>
            ))}
          </div>
        )}
        <form onSubmit={handleSubmit} className="upload-form">
          <div className="form-group">
            <label htmlFor="job-desc">Deskripsi Pekerjaan Target</label>
            <textarea
              id="job-desc"
              placeholder="Tempel deskripsi pekerjaan di sini..."
              value={jobDescription}
              onChange={(e) => setJobDescription(e.target.value)}
              rows="8"
            />
          </div>

          <div className="form-group">
            <label htmlFor="cv-file">Unggah CV Anda (PDF/DOCX)</label>
            <input
              id="cv-file"
              type="file"
              accept=".pdf,.docx"
              onChange={(e) => setSelectedFile(e.target.files[0])}
            />
          </div>

          <button type="submit" disabled={isLoading}>
            {isLoading ? 'Menganalisis...' : 'Analisis Sekarang'}
          </button>
        </form>

        {analysisResult && (
          <div className="result-card">
            <h3>Hasil Analisis</h3>
            <p><strong>Nama File:</strong> {selectedFile.name}</p>
            <p className="score"><strong>Skor Kecocokan:</strong> {analysisResult.score}%</p>
            <div className="recommendations">
              <strong>Rekomendasi:</strong>
              <p>{analysisResult.recommendations}</p>
            </div>
          </div>
        )}

      </header>
    </div>
  );
}

export default App;
