// src/components/SearchBar.js
import React, { useState } from "react";
import axios from "axios";

function SearchBar() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);

  const handleSearch = async () => {
    try {
      setHasSearched(true);
      console.log("API URL:", process.env.REACT_APP_API_URL);

      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/candidates/search?q=${query}`
      );

      console.log("API Response:", res.data);
      setResults(res.data.data || []);
    } catch (err) {
      console.error("Error:", err);
      setResults([]);
    }
  };

  return (
    <div className="p-4">
      <div className="flex items-center mb-4 gap-2">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Cari skill, nama, email..."
          className="border rounded p-2 flex-1"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Cari
        </button>
      </div>

      {results.length > 0 ? (
  <div className="overflow-x-auto mt-8 shadow-2xl rounded-xl border border-gray-700 bg-[#1e293b] backdrop-blur-sm">
        <table className="min-w-full text-sm text-gray-200 divide-y divide-gray-700">
          {/* Header Tabel */}
          <thead className="bg-gray-800/80 text-gray-400 uppercase tracking-wider font-semibold">
            <tr>
              <th className="px-6 py-4 text-left">Nama</th>
              <th className="px-6 py-4 text-left">Email</th>
              <th className="px-6 py-4 text-left">Telepon</th>
              <th className="px-6 py-4 text-center">Match Score</th>
              <th className="px-6 py-4 text-center">Status</th>
              <th className="px-6 py-4 text-left">Hard Skills</th>
            </tr>
          </thead>
          {/* Isi Tabel */}
          <tbody className="divide-y divide-gray-800">
            {results.map((candidate, index) => (
              <tr
                key={candidate.id || index}
                className={`
                  transition-all duration-200 ease-in-out
                  ${index % 2 === 0 ? "bg-gray-900/60" : "bg-gray-800/60"} 
                  hover:bg-gray-700/70 hover:shadow-xl
                `}
              >
                <td className="px-6 py-4 whitespace-nowrap font-medium text-gray-100">
                  {candidate.name}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-gray-300">
                  {candidate.email}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-gray-300">
                  {candidate.phone}
                </td>
                <td className="px-6 py-4 text-center font-extrabold text-lg">
                  <span className="text-cyan-400">
                    {candidate.match_score}
                  </span>
                </td>
                <td
                  className={`px-6 py-4 text-center font-bold uppercase text-xs tracking-wider`}
                >
                  <span
                    className={`
                      inline-flex items-center px-3 py-1.5 rounded-full 
                      ${
                        candidate.status?.includes("passed")
                          ? "bg-green-900/40 text-green-400 ring-1 ring-green-600/50"
                          : "bg-red-900/40 text-red-400 ring-1 ring-red-600/50"
                      }
                    `}
                  >
                    {candidate.status?.replace("_", " ")}
                  </span>
                </td>
                <td className="px-6 py-4 text-gray-400 max-w-xs overflow-hidden text-ellipsis">
                  {candidate.hard_skills?.join(", ") || "-"}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    ) : hasSearched ? (
      <div className="mt-8 p-4 bg-gray-800/70 rounded-lg shadow-lg">
        <p className="text-gray-400 italic text-center">
          Tidak ada kandidat ditemukan. Coba kata kunci atau kriteria lain. üîç
        </p>
      </div>
    ) : null}
    </div>
  );
}

export default SearchBar;
