import React, { useState } from 'react';
import { Search } from 'lucide-react'; // Menggunakan ikon search agar lebih relevan

// HAPUS: import axios, ArrowUpDown, CandidateCard karena tidak diperlukan lagi di sini

// MODIFIKASI: Komponen ini sekarang menerima prop 'onSearch'
function SearchBar({ onSearch }) {
  const [query, setQuery] = useState("");
  
  // MODIFIKASI: Fungsi ini hanya memanggil prop onSearch
  const handleSearchClick = () => {
    onSearch(query);
  };
  
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      onSearch(query);
    }
  };

  return (
    <div className="p-4 w-full max-w-4xl mx-auto">
      <div className="flex items-center mb-6 gap-2">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Cari berdasarkan nama atau skill..."
          className="border rounded-md p-2 flex-1 shadow-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
        />
        <button
          onClick={handleSearchClick}
          className="bg-blue-600 text-white px-5 py-2 rounded-md font-semibold hover:bg-blue-700 transition"
        >
          Cari
        </button>
      </div>
      
      {/* HAPUS: Semua logika untuk menampilkan hasil dan sorting dihapus dari sini */}
    </div>
  );
}

export default SearchBar;
