import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import Layout from "../components/Layout";

// BARU: Impor semua komponen yang dibutuhkan
import SearchBar from "../components/SearchBar";
import CandidateCard from "../features/hr/CandidateCard";
import BulkUploadForm from "../features/hr/BulkUploadForm"; // Asumsi lokasi file BulkUploadForm
import "../features/hr/Dashboard.css"; // Kita pakai lagi CSS dari dashboard

//================================================================
// Komponen Modal bisa kita letakkan di sini atau di file terpisah
//================================================================
function CandidateProfileModal({ candidate, onClose }) {
  if (!candidate) return null;
  const profile =
    typeof candidate.structured_profile_json === "string"
      ? JSON.parse(candidate.structured_profile_json)
      : candidate.structured_profile_json;

  return (
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Profil Kandidat Terstruktur</h3>
          <button onClick={onClose} className="close-btn">
            &times;
          </button>
        </div>
        <div className="modal-body">
          <h4>{candidate.extracted_name || candidate.name}</h4>
          <p>
            <strong>Email:</strong> {candidate.extracted_email || "N/A"}
          </p>
          <p>
            <strong>Skor:</strong> {candidate.match_score}%
          </p>
          <hr />
          <h5>Keterampilan (Skills)</h5>
          <div className="skills-container">
            {profile?.skills?.length > 0 ? (
              profile.skills.map((skill) => (
                <span key={skill} className="skill-tag">
                  {skill}
                </span>
              ))
            ) : (
              <p>Tidak ada data skill.</p>
            )}
          </div>
          <h5>Pengalaman Kerja</h5>
          <ul>
            {profile?.experience?.length > 0 ? (
              profile.experience.map((exp) => (
                <li key={exp.title}>
                  {exp.title} di {exp.company} ({exp.years} tahun)
                </li>
              ))
            ) : (
              <li>Tidak ada data pengalaman.</li>
            )}
          </ul>
          <h5>Pendidikan</h5>
          <p>{profile?.education || "Tidak ada data pendidikan."}</p>
        </div>
      </div>
    </div>
  );
}

//================================================================
// Komponen Utama Halaman HR (YANG DIMODIFIKASI)
//================================================================
export default function HRTest() {
  // PINDAHKAN SEMUA STATE & LOGIKA DARI DASHBOARD KE SINI
  const [candidates, setCandidates] = useState([]);
  const [filteredCandidates, setFilteredCandidates] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCandidate, setSelectedCandidate] = useState(null);

  const API_URL = process.env.REACT_APP_API_URL;

  const fetchCandidates = useCallback(async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(
        `${API_URL}/api/hr/jobs/501/candidates`
      );
      setCandidates(response.data);
      setFilteredCandidates(response.data);
      setError(null);
    } catch (err) {
      setError(
        "Gagal memuat data kandidat. Pastikan server backend sudah berjalan."
      );
      console.error("Error fetching candidates:", err);
    } finally {
      setIsLoading(false);
    }
  }, [API_URL]);

  useEffect(() => {
    fetchCandidates();
  }, [fetchCandidates]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredCandidates(candidates);
      return;
    }
    const lowercasedQuery = query.toLowerCase();
    const results = candidates.filter((candidate) => {
      const nameMatch = (candidate.extracted_name || candidate.name)
        .toLowerCase()
        .includes(lowercasedQuery);
      const profile =
        typeof candidate.structured_profile_json === "string"
          ? JSON.parse(candidate.structured_profile_json)
          : candidate.structured_profile_json;
      const skillsMatch = profile?.skills?.some((skill) =>
        skill.toLowerCase().includes(lowercasedQuery)
      );
      return nameMatch || skillsMatch;
    });
    setFilteredCandidates(results);
  };

  const handleUploadSuccess = () => {
    console.log("Upload berhasil! Me-refresh daftar kandidat...");
    fetchCandidates();
  };

  const handleViewProfile = (candidateId) => {
    const candidateToShow = candidates.find((c) => c.id === candidateId);
    setSelectedCandidate(candidateToShow);
  };

  const handleCloseModal = () => {
    setSelectedCandidate(null);
  };

  return (
    <Layout>
      <div className="p-6">
        <h1 className="text-3xl font-bold text-[#343F3E] mb-2">
          Candidate Dashboard
        </h1>
        <p className="text-[#505A5B] mb-6">
          Accessible by HR and Admin roles only.
        </p>

        {/* Letakkan semua komponen di sini */}
        <BulkUploadForm onUploadSuccess={handleUploadSuccess} />

        <div className="search-and-ranking-container mt-6">
          <h2 className="text-2xl font-semibold">
            Peringkat Kandidat untuk "Senior Software Engineer"
          </h2>
          <p className="mb-4">
            Ditemukan {filteredCandidates.length} dari {candidates.length} total
            kandidat.
          </p>

          <SearchBar onSearch={handleSearch} />

          {/* Tampilkan Loading, Error, atau Daftar Kandidat */}
          <div className="candidate-list mt-4">
            {isLoading && candidates.length === 0 ? (
              <p>Loading candidates...</p>
            ) : error ? (
              <p className="error-message">{error}</p>
            ) : filteredCandidates.length > 0 ? (
              filteredCandidates
                .sort((a, b) => b.match_score - a.match_score)
                .map((candidate) => (
                  <CandidateCard
                    key={candidate.id}
                    candidate={candidate}
                    onViewProfile={() => handleViewProfile(candidate.id)}
                  />
                ))
            ) : (
              <div className="no-results p-6 bg-gray-50 rounded-lg text-center">
                <p>Tidak ada kandidat ditemukan yang cocok dengan kriteria Anda. üîç</p>
              </div>
            )}
          </div>
        </div>

        <CandidateProfileModal
          candidate={selectedCandidate}
          onClose={handleCloseModal}
        />
      </div>
    </Layout>
  );
}
