// src/components/SearchBar.js
import React, { useState } from "react";
import axios from "axios";

function SearchBar() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);

  const handleSearch = async () => {
    try {
      setHasSearched(true);
      console.log("API URL:", process.env.REACT_APP_API_URL);

      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/candidates/search?q=${query}`
      );

      console.log("API Response:", res.data);
      setResults(res.data.data || []);
    } catch (err) {
      console.error("Error:", err);
      setResults([]);
    }
  };

  return (
    <div className="p-4">
      <div className="flex items-center mb-4 gap-2">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Cari skill, nama, email..."
          className="border rounded p-2 flex-1"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Cari
        </button>
      </div>

      {results.length > 0 ? (
        <table className="w-full border-collapse border border-gray-400 mt-4">
          <thead>
            <tr className="bg-gray-100">
              <th className="border border-gray-400 p-2 text-left">Nama</th>
              <th className="border border-gray-400 p-2 text-left">Email</th>
              <th className="border border-gray-400 p-2 text-left">Telepon</th>
              <th className="border border-gray-400 p-2 text-left">Match Score</th>
              <th className="border border-gray-400 p-2 text-left">Status</th>
              <th className="border border-gray-400 p-2 text-left">Hard Skills</th>
            </tr>
          </thead>
<tbody>
  {results.map((candidate) => {
    console.log("Candidate Data:", candidate);
    console.log("structured_profile_json:", candidate.structured_profile_json);

    return (
      <tr key={candidate.id} className="hover:bg-gray-50">
        <td className="border border-gray-400 p-2">{candidate.name}</td>
        <td className="border border-gray-400 p-2">{candidate.email}</td>
        <td className="border border-gray-400 p-2">{candidate.phone}</td>
        <td className="border border-gray-400 p-2">{candidate.match_score}</td>
        <td className="border border-gray-400 p-2 capitalize">
          {candidate.status?.replace("_", " ")}
        </td>
        <td className="border border-gray-400 p-2">
          {candidate.hard_skills?.join(", ") || "-"}
        </td>
      </tr>
    );
  })}
</tbody>

        </table>
      ) : hasSearched ? (
        <p className="text-gray-500 mt-2">Tidak ada kandidat ditemukan.</p>
      ) : null}
    </div>
  );
}

export default SearchBar;
