import axios from "axios";
import React, { useState } from "react";

function UserTest() {
  const [candidateId, setCandidateId] = useState("");
  const [downloadUrl, setDownloadUrl] = useState(null);

  const handleGenerateCV = async () => {
    if (!candidateId) {
      alert("Masukkan Candidate ID terlebih dahulu!");
      return;
    }

    try {
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/hr/cv/generate/${candidateId}`,
        { responseType: "blob" }
      );

      // Buat URL untuk blob file PDF
      const fileURL = window.URL.createObjectURL(new Blob([response.data]));
      setDownloadUrl(fileURL);
    } catch (error) {
      console.error("Error generating CV:", error);
      alert("Gagal generate CV. Periksa ID kandidat atau server backend.");
    }
  };

  return (
    <div className="container">
      <h2>Generate Candidate CV</h2>
      <input
        type="text"
        placeholder="Masukkan Candidate ID"
        value={candidateId}
        onChange={(e) => setCandidateId(e.target.value)}
        className="border p-2 rounded-md mr-2"
      />
      <button
        onClick={handleGenerateCV}
        className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
      >
        Generate CV
      </button>

      {downloadUrl && (
        <div style={{ marginTop: 20 }}>
          <a
            href={downloadUrl}
            download={`Candidate_${candidateId}_CV.pdf`}
            className="text-blue-500 underline"
          >
            Download CV PDF
          </a>
        </div>
      )}
    </div>
  );
}

export default UserTest;
